import { useState, useEffect, Fragment } from "react";
import * as React from "react";
import * as ReactDOM from "react-dom";
import CanvasJSReact from "../assets/canvasjs.react";
import "react-datepicker/dist/react-datepicker.css";
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import ImageListItemBar from '@mui/material/ImageListItemBar';
import SearchBar from "material-ui-search-bar";
import Rating from '@mui/material/Rating';
import Tooltip from '@mui/material/Tooltip';

import { Typography, AppBar, Toolbar } from "@material-ui/core";

import '../assets/css/style.css';
import { PhotoCamera } from "@material-ui/icons";
import StarIcon from '@mui/icons-material/Star';
import IconButton from '@mui/material/IconButton';
import FavoriteIcon from '@mui/icons-material/Favorite';
import {
  getBookData
} from "./API";

var CanvasJSChart = CanvasJSReact.CanvasJSChart;
var FontAwesome = require("react-fontawesome");

import { Button, Card, Container, Row, Col, Table } from "react-bootstrap";

function Dashboard() {
  useEffect(() => {
    window.addEventListener("resize", updateDimensions);
    return () => window.removeEventListener("resize", updateDimensions);
  }, []);

  const [searchBarValue, setSearchBarValue] = useState("");

  const getSearchValue = (bookTitle) => {
    const onSuccess = ({ data }) => {
      console.dir(data);
      const names = data.items.map((row) => ({
        img: row.volumeInfo.imageLinks.smallThumbnail,
        title: row.volumeInfo.title,
        author: row.volumeInfo.authors,
        rating: row.volumeInfo.ratingsCount,
        favorite: false,
        id: row.id
      }));
      setItemData(names);
      setCnt(cnt + 1);
    };
    const onFailure = () => { };
    getBookData(bookTitle, onSuccess, onFailure);
  }
  const breakpoints = {
    xs: 0,
    sm: 600,
    md: 960,
    lg: 1280,
    xl: 1920
  }
  const getColumns = (width) => {
    if (width < breakpoints.sm) {
      return 5
    } else if (width < breakpoints.md) {
      return 6
    } else if (width < breakpoints.lg) {
      return 7
    } else if (width < breakpoints.xl) {
      return 8
    } else {
      return 9
    }
  }
  const [columns, setColumns] = useState(getColumns(window.innerWidth))

  const updateDimensions = () => {
    setColumns(getColumns(window.innerWidth))
  }

  const [itemData, setItemData] = useState([]);

  const [cnt, setCnt] = useState(0)

  useEffect(() => {
  }, [cnt])

  const editFavoriteById = (id) => {
    itemData.map(item => {
      if (item.id === id) {
        item.favorite = !item.favorite;
        return itemData;
      }
    });
    console.table(itemData);
    setCnt(cnt + 1)
  }
  const getTitleTooltip = (title, author) => {
    return "Title: " + title + " | Author: " + author;
  }

  return (
    <>
      <Container style={{ paddingLeft: 15, paddingRight: 15 }}>
        <AppBar position="relative">
          <Toolbar>
            <PhotoCamera />
            <Typography valiant="h6">Book Finder</Typography>
          </Toolbar>
        </AppBar>
        <Row
          style={{ fontSize: "12px", alignItems: "center", marginTop: "15px" }}
        >
          <Col>
            <Card>
              <Card.Header>
                <Row>
                  <Col align="center">
                    Enter Your Book Name
                  </Col>
                </Row>
                <Row>
                  <Col align="center">
                    <SearchBar
                      z-index='9999999'
                      value={searchBarValue}
                      onChange={(newValue) => setSearchBarValue(newValue)}
                      onRequestSearch={() => getSearchValue(searchBarValue)}
                    />
                  </Col>
                </Row>
              </Card.Header>
              <Card.Body>
                <div
                  style={{
                    marginTop: `10px`,
                    marginBottom: `15px`,
                    fontSize: 12,
                  }}
                >
                  {itemData.length > 0 ?
                    <Row align="center" key="itemData-notZero">
                      <ImageList cols={columns}>
                        {itemData.map((item) => (
                          <Col style={{ paddingLeft: 10, paddingRight: 0 }}>
                            <ImageListItem key={item.img} sx={{
                              stroke: "black", "&:hover": { color: "pink" },
                              strokeWidth: 1
                            }}>
                              <div>
                                <Tooltip title="Favorite">
                                  <IconButton
                                    z-index='9999998'
                                    onClick={() => { editFavoriteById(item.id) }}
                                    sx={{
                                      left: 40, top: 50, color: (item.favorite == true ? 'red' : 'grey'),
                                      opacity: (item.favorite == true ? '100%' : '75%'),
                                    }}
                                    aria-label={`love ${item.title}`}
                                  >
                                    <FavoriteIcon sx={{
                                      fontSize: "110%",
                                      stroke: "#ffffff", "&:hover": { color: "pink" },
                                      strokeWidth: (item.favorite == true ? 0 : 1)
                                    }} />
                                  </IconButton>
                                </Tooltip>
                              </div>
                              <Row style={{ marginLeft: `10px`, left: `10px` }}>
                                <Tooltip title={getTitleTooltip(item.title, item.author)}>
                                  <img
                                    style={{
                                      marginTop: `10px`,
                                      flex: 1,
                                      maxWidth: 100,
                                      height: 150,
                                      resizeMode: 'contain',
                                      align: 'center'
                                    }}
                                    src={`${item.img}`}
                                    srcSet={`${item.img}`}
                                    alt={item.title}
                                    loading="lazy"
                                  />
                                </Tooltip>
                              </Row>
                              <Tooltip title={getTitleTooltip(item.title, item.author)}>
                                <ImageListItemBar
                                  subtitle={item.title.substring(0, 8) + "..."}
                                  // subtitle={<span>by: {item.author}</span>}
                                  position="below"
                                />
                              </Tooltip>
                              <Rating name="read-only" value={item.rating} readOnly
                              />
                            </ImageListItem>
                          </Col>
                        ))}
                      </ImageList>
                    </Row>
                    :
                    <Row className="justify-content-md-center">
                      <img style={{
                        marginTop: `10px`,
                        flex: 1,
                        width: 50 * columns,
                        height: `100%`,
                        resizeMode: 'contain',
                        align: 'center'
                      }} src="https://i.pinimg.com/564x/49/e5/8d/49e58d5922019b8ec4642a2e2b9291c2.jpg" />
                    </Row>
                    // <div style={{ display: "flex", justifyContent: "center", alignItems: "center" }}>
                    // </div>
                  }
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );

  //  }
}

export default Dashboard;
ReactDOM.render(<Dashboard />, document.querySelector("#root"));
